import {expect, describe, it} from 'vitest'
import longestPalindrome from './brootforce'
import fastPalindrome from './position'

describe('longest palindromic', () => {
    it('test 1', () => {
        const s = 'babad'

        expect(longestPalindrome(s)).toEqual('bab')
    })

    it('test 2', () => {
        const s = 'cbbd'

        expect(longestPalindrome(s)).toEqual('bb')
    })

    it('test 3', () => {
        const s = 'a'

        expect(longestPalindrome(s)).toEqual('a')
    })

    it('test 4', () => {
        const s =
            ""

        expect(longestPalindrome(s)).toEqual(s)
    })
})

describe('longest fast palindromic', () => {
    it('test 1', () => {
        const s = 'babad'

        expect(fastPalindrome(s)).toEqual('bab')
    })

    it('test 2', () => {
        const s = 'cbbd'

        expect(fastPalindrome(s)).toEqual('bb')
    })

    it('test 3', () => {
        const s = 'a'

        expect(fastPalindrome(s)).toEqual('a')
    })

    it('test 4', () => {
        const s =
            ""

        expect(fastPalindrome(s)).toEqual(s)
    })
})